import groovy.json.JsonSlurper
import groovy.json.JsonBuilder
import groovy.json.JsonOutput

def responseContent = testRunner.testCase.getTestStepByName("GET - Sample Request").getPropertyValue("response")
def response = new JsonSlurper().parseText(responseContent)
log.info(response)

def lat = new JsonOutput().toJson(response.results.location.lat.get(0));
log.info ("   "+"Latitude ="+"   "+lat)

com.eviware.soapui.SoapUI.globalProperties.setPropertyValue('singlelatitude', lat);


def lng = new JsonOutput().toJson(response.results.location.lng.get(0));
log.info ("   "+"Longitude ="+"   "+lng)

com.eviware.soapui.SoapUI.globalProperties.setPropertyValue('singlelongitude', lng);

import groovy.json.JsonSlurper
import groovy.json.JsonBuilder
import groovy.json.JsonOutput

def responseContent = testRunner.testCase.getTestStepByName("GET - Sample Request - Multiple Responses").getPropertyValue("response")
def response = new JsonSlurper().parseText(responseContent)
log.info(response)

for(int i = 0; i  < response.size(); i++)
{
	if ( response.results.get(i).elevation == -50.78903579711914) 
	{
		def lat = response.results.get(i).location.lat

		log.info("   "+"Lat = "+"   "+lat)

		def lng = response.results.get(i).location.lng

		log.info("   "+"Lng = "+"   "+lng)

          com.eviware.soapui.SoapUI.globalProperties.setPropertyValue( "multiplelatitude", lat.toString())

          com.eviware.soapui.SoapUI.globalProperties.setPropertyValue( "multiplelongitude", lng.toString())
	}
}

import groovy.json.JsonSlurper
import groovy.json.JsonBuilder
import groovy.json.JsonOutput

def responseContent = testRunner.testCase.getTestStepByName("get-transit _time").getPropertyValue("response")
def response = new JsonSlurper().parseText(responseContent)
log.info(response)

def lat = new JsonOutput().toJson(response.routes.bounds.northeast.lat.get(0))
log.info("  "+"Lattitude = " +lat)

com.eviware.soapui.SoapUI.globalProperties.setPropertyValue("singlelattitude",lat)

def lng = new JsonOutput().toJson(response.routes.bounds.northeast.lng.get(0))
log.info("  "+"Longitude = " +lng)

com.eviware.soapui.SoapUI.globalProperties.setPropertyValue("singlelongitude",lng)
